    color: #fff;
}

.btn-link :hover{
    color: #dc3545;
    font-weight: 400;
    text-decoration: none;
}
.btn:not(:disabled):not(.disabled) {
    cursor: pointer;
}

.btn-secondary.focus, .btn-secondary:focus, .btn-secondary:hover {
    background-color: #e9ecef;
