css:
/*pc*/@media (min-width: 601px){
  .top-bar {
    background: #ebebeb;}
.top-bar__nav {
  color: #010101;background-color:#ebebeb;}
  .header{background: #E0E0E0;
background: -webkit-linear-gradient(90deg,rgba(224, 224, 224, 1) 0%, rgba(247, 247, 247, 1) 50%, rgba(227, 227, 227, 1) 100%);
background: -moz-linear-gradient(90deg,rgba(224, 224, 224, 1) 0%, rgba(247, 247, 247, 1) 50%, rgba(227, 227, 227, 1) 100%);
background: linear-gradient(90deg,rgba(224, 224, 224, 1) 0%, rgba(247, 247, 247, 1) 50%, rgba(227, 227, 227, 1) 100%);
filter: progid:DXImageTransform.Microsoft.gradient(
  startColorstr="#E0E0E0",
  endColorstr="#E3E3E3",
  GradientType=1
);box-shadow:-6px 15px 14px -9px rgba(0, 0, 0, 0.37);}
  .header__search-btn {font-size: 1.3rem;}
  .header__logo{max-height: 90px;max-width: 300px;}
  .header__search-input {border-radius: 1.5rem;margin-left: 430px; font-size:12px;
    border: 1px solid #c0c0c0;width:400px;}

  .badge-primary {
    background-color: #dc3545;border-radius: 1.5rem;}
  .header__nav a:hover {background:#ff001e;
        color: #fff;text-decoration: none;
    font-weight: 700;}

.header__nav a {
    border-bottom: 6px solid #ff001e;
    color: #000;font-weight: 700;
}
.header__nav--full a {
    padding: 10px 10px 10px;
}
  .header__nav a:hover {
    border-bottom-color: transparent;
}
  .header__submenu {background: #dbdbdb;
    border-top: 1px solid #dbdbdb;margin-top: -5px;
  }}


 /*mobile*/@media (max-width: 600px) {

}}

}

 /*responsivo*/
.prices__value { display: block; font-size: 16px; font-weight: 700; 
 color: #bd3600;}
.prices--big{font-size: 0.9rem;font-weight: 700; color: #bd3600;
}
 .prices__installments{font-size: 14px;font-weight: 300;margin-top:0.1rem; margin-bottom:0.1rem;}
.prices__discount{font-size: 16px;font-weight: 300;color: #ff001e; }
.prices--big .prices__value {  font-size: 17px;}
.product-card__name{font-weight: 400;font-size: 15px;}
.prices:not(.prices--literal) .prices__compare {font-weight: 400;font-size: 14px;
   color: var(--gray);}
.product__sku { color: var(--text-muted); font-size: 12px;}
.product__info{color: #ddd;font-size: 15px;font-weight: 400;}
.products-carousel__title{font-weight: 400;font-size: 18px;
color:#000;}
.product-card__offer-stamp {
    background-color: #dc3545;
  border-radius: 20px;}
.btn-primary:hover {
    background-color: #bd3600;
    border-color: #bd3600;
    color: var(--primary-darken-yiq);
}
.btn-primary {
    background-color: #dc3545;
    border-color: #dc3545;

}
.h1 {
    font-size: 1.5rem;
}
.product__sku {
    color: #b0b2b3;
    font-size: 11px;
}
.prices--big .prices__value {
    font-size: 21px;
}
 .share-network-whatsapp{display:none;}
  .share-network-facebook{display:none;}
 .share-network-twitter{display:none;}
  .share-network-telegram{display:none;}
  .share-network-pinterest{display:none;}
  .share-network-email{display:none;}
  .share-network-sms {display:none;}

.btn-outline-secondary {
    border-color: #dddddd;
    color: var(--secondary);
}
.btn:hover{color: #dc3545;}

.footer{background: #E0E0E0;
background: -webkit-linear-gradient(90deg,rgba(224, 224, 224, 1) 0%, rgba(247, 247, 247, 1) 50%, rgba(227, 227, 227, 1) 100%);
background: -moz-linear-gradient(90deg,rgba(224, 224, 224, 1) 0%, rgba(247, 247, 247, 1) 50%, rgba(227, 227, 227, 1) 100%);
background: linear-gradient(90deg,rgba(224, 224, 224, 1) 0%, rgba(247, 247, 247, 1) 50%, rgba(227, 227, 227, 1) 100%);
filter: progid:DXImageTransform.Microsoft.gradient(
  startColorstr="#E0E0E0",
  endColorstr="#E3E3E3",
  GradientType=1
);border-top: var(--spacer-1) solid #d13447;}

.stamps img {max-width: 70%;}

.footer__title {font-weight: 700;}
.i-chevron-right{font-size:0px;}
.page-title__head{font-size:24px;}
.i-home{color:#dc3545;}
.badge-warning {
    background-color: #959595;
    color: var(--gray-dark);
}
.badge-dark {
    background-color: #cdcdcd;
    color: #2f2f2f;
}
.product__info {
    color: #000000;
    font-size: 15px;
    font-weight: 400;
}
.btn-success {
    background-color: #d13447;
    border-color: #d13447;
    color: var(--white);
}
.btn-success:not(:disabled):not(.disabled):active, .show>.btn-success.dropdown-toggle {
    background-color: #dc3545;
    border-color: #dc3545;
    color: var(--white);
}
.btn-light:{
    background-color: #ddd;
    border-color: #d13447;
    color: #dc3545;
}
.btn-light:hover {
    background-color: #d13447;
    border-color: #d13447;
    color: #ffffff;
}
}
